import React, {useContext} from "react";
import "../../styles/css/style.css";
import {useHistory} from "react-router";
import {useDispatch} from "react-redux";
import {setAuthUser, setIsAuthenticated} from "../../appSlice";
import axios from "axios";
import { GoogleLogin } from 'react-google-login';
import Icon from "../../components/GoogleLogin/Icon";
import { Button } from '@material-ui/core';
import useStyles from './styles';

const Login = () => {
    const history = useHistory();
    const classes = useStyles();

    const dispatch = useDispatch();
    const fetchAuthUser = async () => {
        const response = await axios
            .get(process.env.REACT_APP_URL_API_LOGIN + "/api/v1/auth/user", {
                withCredentials: true,
                headers: {"Content-Type": "application/json"}
            })
            .catch((err) => {
                console.log("Not properly authenticated");
                dispatch(setIsAuthenticated(false));
                dispatch(setAuthUser(null));
                history.push("/login/error");
            });

        if (response && response.data) {
            console.log("User: ", response.data);
            dispatch(setIsAuthenticated(true));
            dispatch(setAuthUser(response.data));
            history.push("/dashboard");
        }
    }
    const redirectToGoogleSSO = async () => {
        let timer = setTimeout(null, 10);
        const googleLoginURL =
            process.env.REACT_APP_URL_API_LOGIN + "/api/v1/login";
        const newWindow = window.open(
            googleLoginURL,
            "_blank",
            "width=500,height=600 "
        );
        if (newWindow) {
            timer = setInterval(() => {
                if (newWindow.closed) {
                    console.log("Yay we're authenticated");
                    fetchAuthUser();
                    if (timer) clearInterval(timer);
                }
            }, 400);
        }
    };

    const googleError = () => {
        console.log("Google Sign In was unsuccessful. Try again later");
    };

    const googleSuccess = (res) => {
        console.log(res);
    };

    return (
        <div className="container-fluid">
            <div className="row">
                <div className="col-6 offset-3 mt-4">
                    <GoogleLogin
                        clientId="99715278971-ddph0g57slft61nl7bgp7mg3n0jqsr9q.apps.googleusercontent.com"
                        render={(renderProps) => (
                            <Button className={classes.googleButton} color="primary" fullWidth
                                    onClick={renderProps.onClick} disabled={renderProps.disabled} startIcon={<Icon />}
                                    variant="contained">
                                Google Sign In
                            </Button>
                        )}
                        onSuccess={googleSuccess}
                        onFailure={googleError}
                        cookiePolicy="single_host_origin"
                    />
                </div>
            </div>
        </div>
    );
};

export default Login;
