import React, { useContext } from "react";
import GoogleButton from "react-google-button";
import "../../styles/css/style.css";
import { useHistory } from "react-router";
import {useDispatch} from "react-redux";
import { setAuthUser, setIsAuthenticated } from "../../appSlice";
import axios from "axios";
import Navbar from "../../components/Navbar/Navbar";
const Login = () => {
  const history = useHistory();

  const dispatch = useDispatch();
  const fetchAuthUser = async () => {
    const response = await axios
        .get(process.env.REACT_APP_URL_API_LOGIN + "/api/v1/auth/user", {withCredentials: true, headers:{"Content-Type": "application/json"}})
        .catch((err) => {
          console.log("Not properly authenticated");
          dispatch(setIsAuthenticated(false));
          dispatch(setAuthUser(null));
          history.push("/login/error");
        });

      if (response && response.data) {
        console.log("User: ", response.data);
        dispatch(setIsAuthenticated(true));
        dispatch(setAuthUser(response.data));
        history.push("/dashboard");
      }
  }
  const redirectToGoogleSSO = async () => {
    let timer = setTimeout(null, 10);
    const googleLoginURL =
      process.env.REACT_APP_URL_API_LOGIN + "/api/v1/login";
    const newWindow = window.open(
      googleLoginURL,
      "_blank",
      "width=500,height=600 "
    );
    if (newWindow) {
      timer = setInterval(() => {
        if (newWindow.closed) {
          console.log("Yay we're authenticated");
          fetchAuthUser();
          if (timer) clearInterval(timer);
        }
      }, 400);
    }
  };

  return (
    <div className="container-fluid">
      <div className="row">
        <div className="col-6 offset-3 mt-4">
         <Navbar />
          <GoogleButton onClick={redirectToGoogleSSO} />
        </div>
      </div>
    </div>
  );
};

export default Login;
