import React, { useContext } from "react";
import GoogleButton from "react-google-button";
import "../../styles/css/style.css";
import { UserContext } from "../../contexts/UserContext";
import { useHistory } from "react-router";
import {useDispatch} from "react-redux";
import { setAuthUser, setIsAuthenticated } from "../../appSlice";
import {FALSE} from "node-sass";

const Login = () => {
  const { setUserIsAuthenticated, setUser } = useContext(UserContext);
  const history = useHistory();

  /*const fetchAuthUser = () => {
    fetch(process.env.REACT_APP_URL_API_LOGIN + "/api/v1/auth/user", {
      method: "GET",
      credentials: "include",
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((response) => {
        console.log(response);
        return response.json();
      })
      .then((response) => {
        if (response) {
          console.log("User :", response);
          setUserIsAuthenticated(true);
          setUser(response);
          history.push("/dashboard");
        }
      })
      .catch((error) => console.error(error));
  }; */

  const dispatch = useDispatch();
  const fetchAuthUser = async () => {
    const response = await axios
        .get("http://localhost:5000/api/v1/auth/user", {withCredentials: true})
        .catch((err) => {
          console.log("Not properly authenticated");
          dispatch(setIsAuthenticated(false));
          dispatch(setAuthUser(null));
          history.push("/login/error");
        });

      if (response && response.data) {
        console.log("User: ", response.data);
        dispatch(setIsAuthenticated(true));
        dispatch(setAuthUser(response.data));
        history.push("/welcome");
      }
  }
  const redirectToGoogleSSO = async () => {
    let timer = setTimeout(null, 10);
    const googleLoginURL =
      process.env.REACT_APP_URL_API_LOGIN + "/api/v1/login";
    const newWindow = window.open(
      googleLoginURL,
      "_blank",
      "width=500,height=600 "
    );
    if (newWindow) {
      timer = setInterval(() => {
        if (newWindow.closed) {
          console.log("Yay we're authenticated");
          fetchAuthUser();
          if (timer) clearInterval(timer);
        }
      }, 500);
    }
  };

  return (
    <div className="container-fluid">
      <div className="row">
        <div className="col-6 offset-3 mt-4">
          <GoogleButton onClick={redirectToGoogleSSO} />
        </div>
      </div>
    </div>
  );
};

export default Login;
